// AI Job Market Analysis Engine
const jobs = [];

// Process Adzuna data
if ($input.all()?.[0]?.json?.results) {
  $input.all()[0].json.results.forEach(job => {
    jobs.push({
      source: 'adzuna',
      title: job.title,
      company: job.company?.display_name,
      location: job.location?.display_name,
      salary_min: job.salary_min,
      salary_max: job.salary_max,
      description: job.description,
      url: job.redirect_url,
      created: job.created,
      category: job.category?.label
    });
  });
}

// Process SerpAPI data
if ($input.all()?.[1]?.json?.jobs_results) {
  $input.all()[1].json.jobs_results.forEach(job => {
    jobs.push({
      source: 'google_jobs',
      title: job.title,
      company: job.company_name,
      location: job.location,
      salary_min: extractSalary(job.salary, 'min'),
      salary_max: extractSalary(job.salary, 'max'),
      description: job.description,
      url: job.share_link,
      created: new Date().toISOString(),
      category: 'technology'
    });
  });
}

// Process RemoteOK data
if ($input.all()?.[2]?.json && Array.isArray($input.all()[2].json)) {
  $input.all()[2].json.slice(1).forEach(job => { // Skip first element (metadata)
    if (job.position) {
      jobs.push({
        source: 'remoteok',
        title: job.position,
        company: job.company,
        location: 'Remote',
        salary_min: job.salary_min,
        salary_max: job.salary_max,
        description: job.description,
        url: job.url,
        created: new Date(job.date).toISOString(),
        category: 'remote'
      });
    }
  });
}

function extractSalary(salaryString, type) {
  if (!salaryString) return null;
  const matches = salaryString.match(/\$([\d,]+)/g);
  if (!matches) return null;
  const amounts = matches.map(m => parseInt(m.replace(/[$,]/g, '')));
  return type === 'min' ? Math.min(...amounts) : Math.max(...amounts);
}

// AI-Powered Market Analysis
const analysis = {
  timestamp: new Date().toISOString(),
  total_jobs: jobs.length,
  sources: [...new Set(jobs.map(j => j.source))],

  // Salary Intelligence
  salary_analysis: analyzeSalaries(jobs),

  // Skill Demand Analysis
  skill_trends: analyzeSkills(jobs),

  // Company Intelligence
  hiring_companies: analyzeCompanies(jobs),

  // Geographic Trends
  location_trends: analyzeLocations(jobs),

  // Market Insights
  market_insights: generateInsights(jobs)
};

function analyzeSalaries(jobs) {
  const salaries = jobs.filter(j => j.salary_min && j.salary_max);
  if (salaries.length === 0) return null;

  const minSalaries = salaries.map(j => j.salary_min).sort((a, b) => a - b);
  const maxSalaries = salaries.map(j => j.salary_max).sort((a, b) => a - b);

  return {
    sample_size: salaries.length,
    min_range: {
      p25: percentile(minSalaries, 0.25),
      p50: percentile(minSalaries, 0.5),
      p75: percentile(minSalaries, 0.75),
      p90: percentile(minSalaries, 0.9)
    },
    max_range: {
      p25: percentile(maxSalaries, 0.25),
      p50: percentile(maxSalaries, 0.5),
      p75: percentile(maxSalaries, 0.75),
      p90: percentile(maxSalaries, 0.9)
    },
    average_min: Math.round(minSalaries.reduce((a, b) => a + b) / minSalaries.length),
    average_max: Math.round(maxSalaries.reduce((a, b) => a + b) / maxSalaries.length)
  };
}

function analyzeSkills(jobs) {
  const skillKeywords = {
    'AI/ML': ['artificial intelligence', 'machine learning', 'ai', 'ml', 'deep learning', 'neural network'],
    'Automation': ['automation', 'n8n', 'zapier', 'workflow', 'orchestration', 'integration'],
    'Programming': ['python', 'javascript', 'typescript', 'node.js', 'react', 'vue', 'angular'],
    'Cloud': ['aws', 'azure', 'gcp', 'google cloud', 'kubernetes', 'docker'],
    'Data': ['data science', 'analytics', 'sql', 'postgresql', 'mongodb', 'elasticsearch'],
    'DevOps': ['ci/cd', 'jenkins', 'github actions', 'terraform', 'ansible']
  };

  const skillCounts = {};
  Object.keys(skillKeywords).forEach(category => {
    skillCounts[category] = 0;
    jobs.forEach(job => {
      const text = (job.title + ' ' + job.description).toLowerCase();
      if (skillKeywords[category].some(keyword => text.includes(keyword))) {
        skillCounts[category]++;
      }
    });
  });

  return Object.entries(skillCounts)
    .sort((a, b) => b[1] - a[1])
    .map(([skill, count]) => ({ skill, count, percentage: Math.round((count / jobs.length) * 100) }));
}

function analyzeCompanies(jobs) {
  const companyCounts = {};
  jobs.forEach(job => {
    if (job.company) {
      companyCounts[job.company] = (companyCounts[job.company] || 0) + 1;
    }
  });

  return Object.entries(companyCounts)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 20)
    .map(([company, count]) => ({ company, job_count: count }));
}

function analyzeLocations(jobs) {
  const locationCounts = {};
  jobs.forEach(job => {
    if (job.location) {
      const location = job.location.split(',')[0].trim(); // Get city/state
      locationCounts[location] = (locationCounts[location] || 0) + 1;
    }
  });

  return Object.entries(locationCounts)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 15)
    .map(([location, count]) => ({ location, job_count: count }));
}

function generateInsights(jobs) {
  const insights = [];

  // Remote work trends
  const remoteJobs = jobs.filter(j => j.location && j.location.toLowerCase().includes('remote')).length;
  const remotePercentage = Math.round((remoteJobs / jobs.length) * 100);
  insights.push(`${remotePercentage}% of AI/automation jobs offer remote work`);

  // Salary insights
  const salaryJobs = jobs.filter(j => j.salary_min);
  if (salaryJobs.length > 10) {
    const avgSalary = Math.round(salaryJobs.reduce((sum, j) => sum + (j.salary_min + j.salary_max) / 2, 0) / salaryJobs.length);
    insights.push(`Average AI/automation salary: $${avgSalary.toLocaleString()}`);
  }

  // Company insights
  const companies = jobs.map(j => j.company).filter(Boolean);
  const uniqueCompanies = new Set(companies).size;
  insights.push(`${uniqueCompanies} companies actively hiring in AI/automation`);

  // Skill demand
  const aiJobs = jobs.filter(j => (j.title + j.description).toLowerCase().includes('artificial intelligence')).length;
  const automationJobs = jobs.filter(j => (j.title + j.description).toLowerCase().includes('automation')).length;
  insights.push(`AI roles: ${aiJobs}, Automation roles: ${automationJobs}`);

  return insights;
}

function percentile(arr, p) {
  const index = Math.ceil(arr.length * p) - 1;
  return arr[index] || 0;
}

return [{
  json: {
    market_analysis: analysis,
    job_listings: jobs.slice(0, 100), // Limit for performance
    generated_at: new Date().toISOString(),
    next_update: new Date(Date.now() + 6 * 60 * 60 * 1000).toISOString() // 6 hours
  }
}];

