// Lead Capture & Engagement Tracker
const userAgent = $('Public API Webhook').item.json.headers['user-agent'] || '';
const ip = $('Public API Webhook').item.json.headers['x-forwarded-for'] || $('Public API Webhook').item.json.headers['x-real-ip'] || 'unknown';
const referer = $('Public API Webhook').item.json.headers.referer || 'direct';

// Analyze user profile
const userProfile = {
  timestamp: new Date().toISOString(),
  ip_address: ip,
  user_agent: userAgent,
  referrer: referer,
  is_bot: /bot|crawler|spider/i.test(userAgent),
  is_mobile: /mobile|android|iphone/i.test(userAgent),
  browser: extractBrowser(userAgent),
  potential_recruiter: isLikelyRecruiter(userAgent, referer),
  engagement_score: calculateEngagementScore(userAgent, referer)
};

function extractBrowser(ua) {
  if (ua.includes('Chrome')) return 'Chrome';
  if (ua.includes('Firefox')) return 'Firefox';
  if (ua.includes('Safari')) return 'Safari';
  if (ua.includes('Edge')) return 'Edge';
  return 'Other';
}

function isLikelyRecruiter(ua, referer) {
  const recruiterIndicators = [
    'linkedin', 'indeed', 'glassdoor', 'monster', 'ziprecruiter',
    'greenhouse', 'lever', 'workday', 'bamboohr', 'recruitee'
  ];

  const uaLower = ua.toLowerCase();
  const refLower = referer.toLowerCase();

  return recruiterIndicators.some(indicator => 
    uaLower.includes(indicator) || refLower.includes(indicator)
  );
}

function calculateEngagementScore(ua, referer) {
  let score = 1; // Base score

  // Higher score for professional referrers
  if (referer.includes('linkedin')) score += 3;
  if (referer.includes('github')) score += 2;
  if (referer.includes('twitter')) score += 1;

  // Higher score for business hours access
  const hour = new Date().getHours();
  if (hour >= 9 && hour <= 17) score += 1;

  // Higher score for desktop users (more likely to be at work)
  if (!ua.includes('mobile')) score += 1;

  return Math.min(score, 10); // Cap at 10
}

return [{
  json: {
    user_profile: userProfile,
    api_response: $('AI Market Analysis Engine').item.json,
    action: userProfile.potential_recruiter ? 'high_priority_lead' : 'standard_visitor'
  }
}];
